mapper.select:
select a.*,b.position,c.companyName,date_format(a.createTime, '%Y-%m-%d %H:%i:%s') createTimeStr from `gas-data` a left join `device-position` b on a.deviceNo= b.deviceNo and a.companyId = b.companyId left join `company` c on a.companyId = c.id where 1=1
and a.companyId = ${companyId}
order by a.companyId,a.deviceNo asc limit ${pageSize} offset ${pageNo*pageSize-pageSize};

	select g.*, p.position from `gas-data` g
	left join `device-position` p on g.deviceNo = p.deviceNo and g.companyId = p.companyId
    where g.companyId = ${companyId} order by g.deviceNo;

mapper.update:
update  `gas-data` set 

	groupId = null


	groupId = ${groupId} 

where deviceNo = ${deviceNo} and companyId = ${companyId};
